import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import CompleteModal from '../components/CompleteModal';

export default function FormPage() {
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const fileInputRef = useRef();
  const navigate = useNavigate();

  // 파일 선택 시 미리보기 처리
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        setImage(ev.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // 클릭 시 파일 선택창 열기
  const handleBoxClick = () => {
    fileInputRef.current.click();
  };

  // 버튼 클릭 시
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setShowModal(true);
    }, 2000); // 2초 후 완료
  };

  // 모달 닫기 시 카드 페이지로 이동
  const handleCloseModal = () => {
    setShowModal(false);
    navigate('/card');
  };

  if (loading) {
    // 로딩 화면
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{ fontSize: 80, marginBottom: 16 }}>💌</div>
        <div style={{ fontSize: 20, marginBottom: 16 }}>청첩장 링크 생성 중...</div>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
      {showModal && (
        <CompleteModal
          onClose={handleCloseModal}
          link="https://mcard.cryucard.com/c/AdeRHoVRYbkLhT4" // 예시 링크
        />
      )}
      <div style={{ fontSize: 22, marginBottom: 32, textAlign: 'center' }}>
        모바일 청첩장을 제작하시려면<br />
        아래의 정보를 입력해 주세요
      </div>
      {/* 업로드 박스 */}
      <div
        style={{
          border: '1px dashed #ddd',
          borderRadius: 8,
          width: 350,
          height: 180,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          marginBottom: 12,
          color: '#b0b0b0',
          fontSize: 16,
          cursor: 'pointer',
          overflow: 'hidden',
          background: '#fafafa',
          position: 'relative'
        }}
        onClick={handleBoxClick}
      >
        {image ? (
          <img
            src={image}
            alt="업로드된 메인사진"
            style={{ width: '100%', height: '100%', objectFit: 'cover' }}
          />
        ) : (
          "메인사진을 업로드해 주세요"
        )}
        <input
          type="file"
          accept="image/*"
          ref={fileInputRef}
          style={{ display: 'none' }}
          onChange={handleImageChange}
        />
      </div>
      <div style={{ fontSize: 13, color: '#444', marginBottom: 24, textAlign: 'center' }}>
        아직 준비된 사진이 없다면?<br />
        아무 사진이나 업로드 후 수정 가능해요!
      </div>
      <div style={{ width: 350, marginBottom: 16 }}>
        <div style={{ fontWeight: 500, marginBottom: 6 }}>신랑 성함</div>
        <div style={{ display: 'flex', gap: 8 }}>
          <input placeholder="신랑 성" style={{ flex: 1, padding: 10, borderRadius: 6, border: '1px solid #ddd', fontSize: 16 }} />
          <input placeholder="신랑 이름" style={{ flex: 2, padding: 10, borderRadius: 6, border: '1px solid #ddd', fontSize: 16 }} />
        </div>
      </div>
      <div style={{ width: 350, marginBottom: 32 }}>
        <div style={{ fontWeight: 500, marginBottom: 6 }}>신부 성함</div>
        <div style={{ display: 'flex', gap: 8 }}>
          <input placeholder="신부 성" style={{ flex: 1, padding: 10, borderRadius: 6, border: '1px solid #ddd', fontSize: 16 }} />
          <input placeholder="신부 이름" style={{ flex: 2, padding: 10, borderRadius: 6, border: '1px solid #ddd', fontSize: 16 }} />
        </div>
      </div>
      <button
        style={{
          width: 350,
          background: '#1a2d13',
          color: '#fff',
          border: 'none',
          borderRadius: 6,
          padding: '14px 0',
          fontSize: 20,
          cursor: 'pointer'
        }}
        onClick={handleSubmit}
      >
        청첩장 링크 생성하기
      </button>
    </div>
  );
}
